    return city, (data[(data['Khu vực'] == city) & (data['Năm'] == year_last)]['Dân số trung bình (Nghìn người)'].values[0] - data[(data['City'] == city) & (data['Year'] == year_first)]['Dân số trung bình (Nghìn người)'].values[0])/(years[-1] - years[0])
